        <script>

            function initMap() {
                var myLatLng = {lat: 44.809750, lng: 20.473434};
                var map = new google.maps.Map(document.getElementById('map'), {
                    zoom: 15,
                    center: myLatLng
                });

                var marker = new google.maps.Marker({
                    position: myLatLng,
                    map: map,
                    title: 'Hello World!'
                });
            }
        </script>
        <script async defer
                src="https://maps.googleapis.com/maps/api/js?key=AIzaSyDN-yO46XaJJwmnjN0rf3-Fv4_H_POxU9s&callback=initMap">
